mapper.insert:

	select LAST_INSERT_ID() board_image_file_id


insert into tb_board_image_file (
	 board_token
	,original_filename
	,real_filename
	,content_type
	,size
) values (
	 #{board_token}
	,#{original_filename}
	,#{real_filename}
	,#{content_type}
	,#{size}
)

mapper.update:
update tb_board_image_file set
	 bno = #{bno}
where board_token = #{board_token}

mapper.delete:
delete from tb_board_image_file  
where board_token in 

	#{item.board_token}
